INSERT INTO `preguntas` (`_id`, `pregunta`, `respuesta`, `activa`, `orden`) VALUES
(8, '¿RECIBEN TRAJETAS DE CRÉDITO?', 'Recibimos tarjetas de crédito VISA, MASTERCARD, FIEL, DATA200, NEVADA hasta 3 cuotas. También recibimos préstamos de la caja de acción social y MercadoPago.', 'SI', 1),
(9, '¿QUÉ HORARIO DE ATENCIÓN TIENEN?', 'Nuestro local permanece abierto de LUNES a VIERNES de 9 Hrs A 12:40 Hrs y de 16:00 Hrs A 19:00 Hrs. SÁBADOS de 9 Hrs a 13 Hrs.', 'SI', 2),
(10, '¿HACEN ENVÍOS A DOMICILIO? ', 'La empresa no cuenta con servicio propio de envío a domicilio. Sin embargo, contamos con un prestador particular del servicio que encontrará en caso de necesitarlo.', 'SI', 3),
(11, '¿HACEN CORTES A MEDIDA?', 'Contamos con dos máquinas para hacer cortes a medida con precisión milimétrica. El servicio de corte a medida incluye el optimizado del material a cortar a fines de aprovechar su superficie lo mejor posible. ', 'SI', 4),
(12, '¿PUEDO RETIRAR LOS CORTES EN EL MOMENTO?', 'Cuando los cortes son pocos y sencillos o son cortes para cargar un producto,  se hacen inmediatamente.  En caso de planillas de corte complejos se estima una demora de 2 días aproximadamente.', 'SI', 5),
(13, '¿CUÁNTOS M2 RINDE UN LITRO DE BARNIZ?', 'Rinde de 12 a 14 m2 aproximadamente por mano.', 'SI', 6),
(14, '¿SE PUEDE COMPRAR UN RECORTE PEQUEÑO DE MADERA?', 'No vendemos recortes pequeños de madera con una medida específica. Se puede comprar una fracción pequeña estandarizada de madera y de allí cortar la pieza que desea.', 'SI', 7),
(15, '¿LA EMPRESA TIENE ESTACIONAMIENTO PROPIO?', 'Contamos con una amplia playa de estacionamiento privado para clientes ubicada en la parte posterior de la empresa. Para llegar consulte nuestra sección DONDE ESTAMOS.', 'SI', 8),
(16, '¿HACEN PRESUPUESTOS?', 'Si hacemos presupuestos y optimizaciones de placas sin costo alguno. Solicitamos que concurra por la empresa a primera hora del día. De lo contrario puede enviar su consulta por mail maderas.lliteras@gmail.com o rlliteras@speedy.com.ar y le responderemos lo antes posible.', 'SI', 9);
